    import React, { useEffect, useState } from "react";
    import axios from "axios";
    import Apikey from './apiKey';

    const Box = ({ CityName }) => {

    //   const Values={main:{humidity:'',pressure:''},sys:{country:''}}
    const [pressure,setPressure]=useState();
    const [humidity,setHumidity]=useState("");
    const [country,setCountry]=useState("");
    const [temperature,setTemperature]=useState();

    //   const [Data, setData] = useState(Values);
    const [City, Setcity] = useState("");

    const getData = async (city) => {
        if (!city) return;
        try {
        const res = await axios.get(
            `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=` + Apikey
        );
        console.log(res);

        console.log(res.data.name)
        setHumidity(res.data.main.humidity)
        // console.log(humidity)
        setPressure(res.data.main.pressure)  
        // setCountry(res.sys.country)
        // Setcity(res.data.name)
        setTemperature(res.data.main.temp)

        } catch (err) {
        console.log(err);
        }
    };

    useEffect(() => {
        getData(CityName);
    }, [CityName, pressure, humidity, country, City, temperature]); // Include CityName in the dependency array

    return (
        <div className="col-md-12 text-center mt-5">
        <div className="shadow rounded resultBox">
            <img
            className="weatherIcon"
            src="https://cdn.jim-nielsen.com/ios/1024/weather-2019-02-07.png"
            alt="Weather Icon"
            />
            <h5 className="Weathercity">City:-{City}</h5>
        
        </div>
        </div>
    );
    };

    export default Box;



