import React, { useEffect, useState } from "react";
import axios from "axios";
import Apikey from './apiKey';

const Box = ({ CityName }) => {
  const [Data, setData] = useState({});
  const [City, Setcity] = useState("");

  const getData = async (city) => {
    if (!city) return;
    try {
      const res = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=` + Apikey
      );
      console.log(res.data);
      setData(res.data);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getData(CityName);
  }, [CityName]); // Include CityName in the dependency array

  return (
    <div className="col-md-12 text-center mt-5">
      <div className="shadow rounded resultBox">
        <img
          className="weatherIcon"
          src="https://cdn.jim-nielsen.com/ios/1024/weather-2019-02-07.png"
          alt="Weather Icon"
        />
        <h5 className="Weathercity">City:-{(Data.name)}</h5>
        <h6 className="Weathertemperature">Temperature: {(Data.temp - 273.15).toFixed(2)}</h6>
        <h6 >Pressure:-{Data.humidity }</h6>
        <h6 >Humidity:-{Data.pressure }</h6>
      </div>
    </div>
  );
};

export default Box;
